class NewsModel {  String? result;  List<Records>? records;  int? totalRecordCount;  NewsModel({this.result, this.records, this.totalRecordCount});  NewsModel.fromJson(Map<String, dynamic> json) {    result = json['Result'];    if (json['Records'] != null) {      records = <Records>[];      json['Records'].forEach((v) {        records!.add(new Records.fromJson(v));      });    }    totalRecordCount = json['TotalRecordCount'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['Result'] = this.result;    if (this.records != null) {      data['Records'] = this.records!.map((v) => v.toJson()).toList();    }    data['TotalRecordCount'] = this.totalRecordCount;    return data;  }}class Records {  String? title;  String? description;  String? category;  String? image;  String? publishing;  int? order;  bool? showInHomePage;  Records(      {this.title,      this.description,      this.category,      this.image,      this.publishing,      this.order,      this.showInHomePage});  Records.fromJson(Map<String, dynamic> json) {    title = json['Title'];    description = json['Description'];    category = json['Category'];    image = json['Image'];    publishing = json['Publishing'];    order = json['Order'];    showInHomePage = json['ShowInHomePage'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['Title'] = this.title;    data['Description'] = this.description;    data['Category'] = this.category;    data['Image'] = this.image;    data['Publishing'] = this.publishing;    data['Order'] = this.order;    data['ShowInHomePage'] = this.showInHomePage;    return data;  }}