import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:islamic_unveristy/moduls/home/views/screens/show_note.dart';import 'package:islamic_unveristy/moduls/home/views/screens/write_note.dart';import 'package:islamic_unveristy/utils/themes/light_theme.dart';class NoteScreen extends StatelessWidget {  const NoteScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        leading: GestureDetector(          onTap: () {            Get.back();          },          child: Icon(            Icons.arrow_back_ios,            size: 20.w,          ),        ),        actions: [          IconButton(              onPressed: () {                Get.to(WriteNote());              },              icon: Icon(                Icons.add,                size: 22.w,              ))        ],        title: Text(          "notes".tr,          style: Theme.of(context)              .textTheme              .subtitle1!              .copyWith(fontWeight: FontWeight.w500, color: Colors.white),        ),      ),      body: Padding(        padding: EdgeInsets.symmetric(vertical: 14.h, horizontal: 16.w),        child: ListView.separated(          itemBuilder: (BuildContext context, int index) {            return GestureDetector(                onTap: () {                  Get.to(ShowNote());                },                child: Container(                  width: double.infinity,                  decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(4),                    color: HexaColor.fromHexa('##F2F6F9'),                  ),                  padding: EdgeInsets.symmetric(horizontal: 8.w, vertical: 8.h),                  child: Row(                    crossAxisAlignment: CrossAxisAlignment.start,                    mainAxisAlignment: MainAxisAlignment.start,                    children: [                      Expanded(                          child: Column(                        mainAxisAlignment: MainAxisAlignment.start,                        crossAxisAlignment: CrossAxisAlignment.start,                        children: [                          Text(                            "ملاحظات دراسية",                            style: Theme.of(context).textTheme.subtitle1,                          ),                          SizedBox(                            height: 9.h,                          ),                          Text(                            "هذا النص هو مثال لنص يمكن أن يستبدل في نفس المساحة لقد تم توليد هذا النص من مولد النص ...",                            style: Theme.of(context)                                .textTheme                                .subtitle2!                                .copyWith(color: HexaColor.fromHexa('#4F4F4F')),                          ),                        ],                      )),                    ],                  ),                ));          },          separatorBuilder: (BuildContext context, int index) {            return SizedBox(              height: 14.h,            );          },          itemCount: 4,        ),      ),    );  }}